mapper.insert:
insert into sample_writer_tb(
	BOARD_WRITER 
 	,BOARD_WRITER_NAME
	,BOARD_WRITER_PW 
	,BOARD_WRITER_PHONE
	,BOARD_WRITER_EMAIL
	,FILE_IDX
	,BOARD_WRITER_JOIN_DATE
	,SALT
)
values (
	#{boardWriter}
	,#{boardWriterName}
	,#{boardWriterPw}
	,#{boardWriterPhone}
	,#{boardWriterEmail} 
	,#{fileIdx}
	,#{BoardWriterJoinDate}
	,#{salt} 
	
)
 SELECT LAST_INSERT_ID()

INSERT INTO sample_file_tb
	(	FILE_ORIGINAL_NAME
		, FILE_CHANGED_NAME
		, FILE_PATH
	) VALUES(
		#{fileOriginalName}
		, #{fileChangeName}
		, #{filePath}
	)
 SELECT LAST_INSERT_ID()

mapper.select:
SELECT
	BOARD_WRITER_IDX
	,BOARD_WRITER
	,BOARD_WRITER_NAME
	,BOARD_WRITER_PHONE
	,BOARD_WRITER_EMAIL
FROM
	sample_writer_tb


	 AND BOARD_WRITER_IDX         = #{boardWriterIdx}       
	 AND BOARD_WRITER      = #{boardWriter}    
	 AND BOARD_WRITER_NAME       = #{boardWriterName}     
	 AND BOARD_WRITER_PHONE    = #{boardWriterPhone}  
	 AND BOARD_WRITER_EMAIL  = #{boardWriterEmail}

mapper.update:
UPDATE 
	sample_writer_tb 
SET
	BOARD_WRITER_NAME = #{boardWriterName}
	, BOARD_WRITER_PW = #{boardWriterPw}
	, BOARD_WRITER_PHONE = #{boardWriterPhone}
	, BOARD_WRITER_EMAIL = #{boardWriterEmail}
WHERE 
	BOARD_WRITER = #{boardWriter}
